import React, { useState } from "react";
import "./styles.css";
import song from './sufi.mp3';
import Waveform from "./Waveform";

 const url = "https://www.mfiles.co.uk/mp3-downloads/gs-cd-track2.mp3";

export default function App() {
  const [selectedTrack, setSelectedTrack] = useState(song);
  const handleSubmit=(e)=>{
    setSelectedTrack(e.target.value)
  };
  return (
    <div className="App">
     <h1> My Player</h1>
     <h3>Upload Audio</h3>
     <form >     
                <input type="file" value={selectedTrack} onChange={(e) => setSelectedTrack(e.target.value)}/>
                <input type="submit" value="Add a Song"/>
            </form>
      <Waveform url={selectedTrack} />     
      <br />
    </div>
  );
}
